import {React,useState} from 'react'
import SearchRes from './SearchRes';

export default function ShowTest(props) {
    const [show,setShow]=useState(false)
    const showHandler=()=>{
        setShow(true);
      }
      const hideHandler=()=>{
        setShow(false);
      }
  return (
    <div>
    {show && <SearchRes searchval={props.searchval} searchAnime={props.searchAnime} isLoader={props.isLoader} onHide={hideHandler}></SearchRes> }

    </div>
  )
}
