import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
import HeaderMenu from '../HeaderMenu'
import SignUpModal from '../UI/SignUpModal'
import classes from './SignUp.module.css'
import Axios from '../../config/Axios';
export default function SignUp(props) {
const [name,setName]=useState('');
const [email,setEmail]=useState('');
const [password,setPassword]=useState('');
const [conPassword,setConPassword]=useState('');
const loginHandler=()=>{
  props.onHide();
  props.onLoginShow()
}
const nameHandler=(e)=>{
  setName(e.target.value)
}
const emailHandler=(e)=>{
setEmail(e.target.value)
}
const passwordHandler=(e)=>{
  setPassword(e.target.value)
}
const conPasswordHandler=(e)=>{
  setConPassword(e.target.value)
}
const submitHandler=(e)=>{
  e.preventDefault();
  const data={
           name:name,
           email:email,
           password:password,
           confirm_password:conPassword
  }
  Axios.post('/register').then(res=>{
    console.log(res)
  })
 console.log(data)
 
}
  return (
    <React.Fragment>
  <SignUpModal onHide={props.onHide}>
      <div className={classes['modal-register-section']}>
          <div className={classes['modal-header']}>
              <h5 className={classes['modal-title']}>Create Account</h5>
              <button className={classes['close']} onClick={props.onHide}>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="black" class="bi bi-x" viewBox="0 0 16 16">
  <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
</svg>
              </button>
          </div>
          <div className={classes['modal-body']}>
          <form className={classes['form-section']} onSubmit={submitHandler}>
          <div className={classes['form-group']}>
          <label className={classes['prelabel']} htmlFor='username'>
               Your name
              </label>
              <input type='text' className={classes['form-control']} onChange={nameHandler}  />
              </div>
              <div className={classes['form-group']}>
          <label className={classes['prelabel']} htmlFor='email'>
               Email Address
              </label>
              <input type='email' className={classes['form-control']}  onChange={emailHandler}  />
              </div>
              <div className={classes['form-group']}>
          <label className={classes['prelabel']} htmlFor='password'>
               Password
              </label>
              <input type='password' className={classes['form-control']} onChange={passwordHandler} />
              </div>
              <div className={classes['form-group']}>
          <label className={classes['prelabel']} htmlFor='conpassword'>
               Confirm Password
              </label>
              <input type='password' className={classes['form-control']} onChange={conPasswordHandler}  />
              </div>
              <div className={classes['form-group']}>
                  <button className={classes['btn-primary']}>Register</button>
              </div>
          </form>
          </div>
          
          <div className={classes['modal-footer']}>
              Have an account ?
              <span className={classes['login-tab']} onClick={loginHandler}>Login</span>
          </div>
      </div>
      </SignUpModal>
    </React.Fragment>
  )
}
