  storage=localStorage.setItem('Favourite',JSON.stringify(fav))
  ctx.addItem(anime)

}
console.log(storage)
const removeFavHandler=(anime)=>{
  const filteredList=fav.filter((f)=>f.id!==anime.id);
  setFav(filteredList)
  ctx.removeItem(anime)
}
