import React from 'react'
import Slider from "react-slick";
import classes from './Popular.module.css';
export default function Popular() {
    function SampleNextArrow(props) {
        const { className, style, onClick } = props;
        return (
          // <div
          //   className={className}
          //   style={{ ...style,  background: "red",borderRadius:'50%' }}
          //   onClick={onClick}
          // />
          
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-caret-right" viewBox="0 0 16 16" className={classes.nextBtn}  onClick={onClick}>
        <path d="M6 12.796V3.204L11.481 8 6 12.796zm.659.753 5.48-4.796a1 1 0 0 0 0-1.506L6.66 2.451C6.011 1.885 5 2.345 5 3.204v9.592a1 1 0 0 0 1.659.753z"/>
      </svg>
      
       
        );
      }
      
      function SamplePrevArrow(props) {
        const { className, style, onClick } = props;
        return (
        //   .swiper-button {
        //     background: #2a2b31;
        //     color: #fff;
        //     font-size: 18px;
        //     width: 40px;
        //     height: 40px;
        //     line-height: 40px;
        //     text-align: center;
        //     border-radius: 6px;
        //     display: inline-block;
        //     margin: 3px 0;
        // }
          // <div
          //   className={className}
          //   style={{ ...style, display: "block", background: "green" }}
          //   onClick={onClick}
          // />
        
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#fff" class="bi bi-caret-left" viewBox="0 0 16 16" className={classes.nextBtn}    onClick={onClick}>
          <path d="M10 12.796V3.204L4.519 8 10 12.796zm-.659.753-5.48-4.796a1 1 0 0 1 0-1.506l5.48-4.796A1 1 0 0 1 11 3.204v9.592a1 1 0 0 1-1.659.753z"/>
        </svg>
        );
      }
      
    const settings = {
          className: "center",
          centerMode: true,
          infinite: true,
          centerPadding: "60px",
          slidesToShow: 3,
          speed: 500,
          nextArrow:<SampleNextArrow/>,
          prevArrow:<SamplePrevArrow/>
    };
  return (
    <div>
    <h2>Center Mode</h2>
    <Slider {...settings}>
      <div>
        <h3>1</h3>
      </div>
      <div>
        <h3>2</h3>
      </div>
      <div>
        <h3>3</h3>
      </div>
      <div>
        <h3>4</h3>
      </div>
      <div>
        <h3>5</h3>
      </div>
      <div>
        <h3>6</h3>
      </div>
    </Slider>
  </div>
  )
}
