import React, { useEffect, useRef, useState } from 'react'
import classes from './Header.module.css';
import logo from '../assets/logo.png';
import Axios from '../config/Axios';

function Header() {

    const [api,setApi]=useState("/anime");
    const [category,setCategory]=useState([]);
    useEffect(() => {
      Axios.get("/category").then((res)=>{
          setCategory(res.data.data);
      })
    }, [])
    console.log(category);
    const searchRef=useRef();
    const clickHandler=(event)=>{
        event.preventDefault();
        console.log(searchRef.current.value);
    }
        
    return (
       
        <div>
            <div id='header' className={classes['header-home']}>
                <div className='con'>
                    <div id='mobile_menu'>
                      
                    </div>
                    <img src={logo} alt='logo'/>
                    <div className={classes.search}>
                        <div className={classes['search-content']}>
                            <form>
                                <button className={classes['filter-icon']}>Filter</button>
                                <input className='form-control' type='text' ref={searchRef} placeholder='Search Anime'/>
                                <button className={classes['search-icon']} onClick={clickHandler}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
  <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
</svg>
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className={classes['header_right']}>
                    <div className={classes['header_right-user']}>
                        <button className={classes['btn-user']}>Login</button>
                    </div>
                    </div>
                </div>
            </div>
            
        </div>
    )
}

export default Header
