import React from 'react'
import classes from './Trending.module.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

import Slider from "react-slick";
import bg1 from '../assets/bg1.jpg';
import demon from '../assets/demon.jpg';
import title from '../assets/demontitle.png';
function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    // <div
    //   className={className}
    //   style={{ ...style,  background: "red",borderRadius:'50%' }}
    //   onClick={onClick}
    // />
    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-caret-right" viewBox="0 0 16 16" className={classes.pbtn}  onClick={onClick}>
  <path d="M6 12.796V3.204L11.481 8 6 12.796zm.659.753 5.48-4.796a1 1 0 0 0 0-1.506L6.66 2.451C6.011 1.885 5 2.345 5 3.204v9.592a1 1 0 0 0 1.659.753z"/>
</svg>
 
  );
}

function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
  //   .swiper-button {
  //     background: #2a2b31;
  //     color: #fff;
  //     font-size: 18px;
  //     width: 40px;
  //     height: 40px;
  //     line-height: 40px;
  //     text-align: center;
  //     border-radius: 6px;
  //     display: inline-block;
  //     margin: 3px 0;
  // }
    // <div
    //   className={className}
    //   style={{ ...style, display: "block", background: "green" }}
    //   onClick={onClick}
    // />
  
    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#fff" class="bi bi-caret-left" viewBox="0 0 16 16" className={classes.nbtn} onClick={onClick}>
    <path d="M10 12.796V3.204L4.519 8 10 12.796zm-.659.753-5.48-4.796a1 1 0 0 1 0-1.506l5.48-4.796A1 1 0 0 1 11 3.204v9.592a1 1 0 0 1-1.659.753z"/>
  </svg>
  );
}

export default function Trending() {
  const settings = {

    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    // autoplay:true,
    // autoplaySpeed:2000,
    
    nextArrow: <SampleNextArrow  />,
    prevArrow: <SamplePrevArrow />
  };
  return (
    
      
        
        <Slider {...settings} className={classes['s-container']}  >
        <div>
          <div className={classes['swipe-slider']} style={{ backgroundImage: `url(${demon})` }}>
        <div className={classes['item-content']}>
          <div className={classes['des-heading']}>
           <img src={title} alt='hi'/>
          </div>
         <div className={classes['des-titleinfo']}>
         <div className={classes['row des-title']}>
         Demon Slayer:Kimetsunoyaiba
         </div>
         </div>
          </div>
        
         </div>
         </div>
         <div className={classes['slide-detail']}>
         <h1>2</h1>
         </div>
        
        </Slider>

       
      
  )
}
